import React, { Component } from "react";
import { FormattedMessage } from "react-intl";
import { connect } from "react-redux";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from "reactstrap";

class ModalUser extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email:'',
      password:'',
      firstName:'',
      lastName:'',
      address:'',
    };
  }
  componentDidMount() {}
  toggle = () => {
    this.props.toggleFromParent();
  };

  handleOnChangeInput=(event,id) => {
  let copyState = {...this.state};
  copyState[id] = event.target.value;
  this.setState
  }

  render() {
    return (
      <Modal
        isOpen={this.props.isOpen}
        toggle={() => {
          this.toggle();
        }}
        className="modal-user-container"
        size="lg"
        
      >
        <ModalHeader
          toggle={() => {
            this.toggle();
          }}
        >
          Modal title
        </ModalHeader>
        <ModalBody>
        <div className="container">
            <div className="row">
                <div className="modal-user-body">
                    <div className="input-container">
                        <lable>Email</lable>
                        <input type="text" onChange={(event)=>{this.handleOnChangeInput(event,"email")}}></input>
                    </div>
                    <div className="input-container">
                        <lable>Password</lable>
                        <input type="password" onChange={(event)=>{this.handleOnChangeInput(event,"password")}}></input>
                    </div>
                    <div className="input-container">
                        <lable>First name</lable>
                        <input type="text" onChange={(event)=>{this.handleOnChangeInput(event,"firstName")}}></input>
                    </div>
                    <div className="input-container">
                        <lable>Last name</lable>
                        <input type="text" onChange={(event)=>{this.handleOnChangeInput(event,"lastName")}}></input>
                    </div>
                    <div className="input-container max-width-input">
                        <lable>Address</lable>
                        <input type="text" onChange={(event)=>{this.handleOnChangeInput(event,"address")}}></input>
                    </div>
                </div>            
            </div>
        </div>
        </ModalBody>
        <ModalFooter>
          <Button
            color="primary"
            className="px-3"
            onClick={() => {
              this.toggle();
            }}
          >
            Add new
          </Button>{" "}
          <Button
            color="secondary"
            className="px-3"
            onClick={() => {
              this.toggle();
            }}
          >
            Cancel
          </Button>
        </ModalFooter>
      </Modal>
    );
  }
}

const mapStateToProps = (state) => {
  return {};
};

const mapDispatchToProps = (dispatch) => {
  return {};
};

export default connect(mapStateToProps, mapDispatchToProps)(ModalUser);
